		private IEnumerator LoadMusic_InSteps()
		{
			yield return null;
			IEnumerator sub1 = audioSystem.PrepareWaveBank();
			while (sub1.MoveNext())
			{
				yield return sub1.Current;
			}
			for (int i = 1; i < 92; i++)
			{
				audioSystem.LoadCue(i, "Music_" + i);
				yield return null;
			}
			audioSystem.LoadFromSources();
		}

