		public static bool IsNPCActiveAndOneOfTypes(int npcIndex, params int[] types)
		{
			if (npcIndex < 0)
			{
				return false;
			}
			NPC nPC = npc[npcIndex];
			if (!nPC.active)
			{
				return false;
			}
			for (int i = 0; i < types.Length; i++)
			{
				if (nPC.type == types[i])
				{
					return true;
				}
			}
			return false;
		}

