		public static string GetInputText(string oldString, bool allowMultiLine = false)
		{
			if (!hasFocus)
			{
				return oldString;
			}
			inputTextEnter = false;
			inputTextEscape = false;
			string text = oldString;
			string text2 = "";
			if (text == null)
			{
				text = "";
			}
			bool flag = false;
			if (inputText.IsKeyDown(Keys.LeftControl) || inputText.IsKeyDown(Keys.RightControl))
			{
				if (inputText.IsKeyDown(Keys.Z) && !oldInputText.IsKeyDown(Keys.Z))
				{
					text = "";
				}
				else if (inputText.IsKeyDown(Keys.X) && !oldInputText.IsKeyDown(Keys.X))
				{
					Platform.Get<IClipboard>().set_Value(oldString);
					text = "";
				}
				else if ((inputText.IsKeyDown(Keys.C) && !oldInputText.IsKeyDown(Keys.C)) || (inputText.IsKeyDown(Keys.Insert) && !oldInputText.IsKeyDown(Keys.Insert)))
				{
					Platform.Get<IClipboard>().set_Value(oldString);
				}
				else if (inputText.IsKeyDown(Keys.V) && !oldInputText.IsKeyDown(Keys.V))
				{
					text2 = PasteTextIn(allowMultiLine, text2);
				}
			}
			else
			{
				if (inputText.PressingShift())
				{
					if (inputText.IsKeyDown(Keys.Delete) && !oldInputText.IsKeyDown(Keys.Delete))
					{
						Platform.Get<IClipboard>().set_Value(oldString);
						text = "";
					}
					if (inputText.IsKeyDown(Keys.Insert) && !oldInputText.IsKeyDown(Keys.Insert))
					{
						text2 = PasteTextIn(allowMultiLine, text2);
					}
				}
				for (int i = 0; i < keyCount; i++)
				{
					int num = keyInt[i];
					string text3 = keyString[i];
					if (num == 13)
					{
						inputTextEnter = true;
					}
					else if (num == 27)
					{
						inputTextEscape = true;
					}
					else if (num >= 32 && num != 127)
					{
						text2 += text3;
					}
				}
			}
			keyCount = 0;
			text += text2;
			oldInputText = inputText;
			inputText = Keyboard.GetState();
			Keys[] pressedKeys = inputText.GetPressedKeys();
			Keys[] pressedKeys2 = oldInputText.GetPressedKeys();
			if (inputText.IsKeyDown(Keys.Back) && oldInputText.IsKeyDown(Keys.Back))
			{
				backSpaceRate -= 0.05f;
				if (backSpaceRate < 0f)
				{
