		private void SortStardustDragonProjectiles(List<int> list)
		{
			List<List<int>> list2 = new List<List<int>>();
			for (int i = 0; i < list.Count; i++)
			{
				int num = list[i];
				if (Main.projectile[num].type == 628)
				{
