						list.Remove(byUUID);
						byUUID = Projectile.GetByUUID(Main.projectile[byUUID].owner, Main.projectile[byUUID].ai[0]);
					}
					List<int> list4 = new List<int>();
					for (int num2 = list3.Count - 2; num2 >= 0; num2--)
					{
						list4.Add(list3[num2]);
					}
					list4.Add(list3[list3.Count - 1]);
