			int num5 = (int)((float)(255 - bgColorForStars.B - 100) * theStar.twinkle * num2);
			num3 = (num3 + num5 + num4) / 3;
			if (num3 <= 0)
			{
				return;
			}
			num3 = (int)((double)num3 * 1.4);
