					value.R = (byte)((float)(int)color.R * (1f - num * 0f) * theStar.twinkle * theStar.twinkle);
				}
				color = Color.Lerp(color, value, num);
				if (artificial)
				{
					color *= num;
				}
				else
				{
					color *= 1f - num;
				}
