			if (num < 1f)
			{
				num = 1f;
			}
			if (invasionX > (double)spawnTileX)
			{
				invasionX -= num;
				if (invasionX <= (double)spawnTileX)
				{
					invasionX = spawnTileX;
					InvasionWarning();
				}
				else if (num > 0f)
				{
					invasionWarn--;
				}
			}
			else if (invasionX < (double)spawnTileX)
			{
				invasionX += num;
				if (invasionX >= (double)spawnTileX)
				{
					invasionX = spawnTileX;
					InvasionWarning();
				}
				else if (num > 0f)
				{
					invasionWarn--;
				}
			}
			if (invasionWarn <= 0)
			{
				invasionWarn = 3600;
				InvasionWarning();
			}
		}

