		public Color GetNPCColorTintedByBuffs(Color npcColor)
		{
			if (!canDisplayBuffs)
			{
				return npcColor;
			}
			float num = 1f;
			float num2 = 1f;
			float num3 = 1f;
			float a = 1f;
			if (poisoned)
			{
				num *= 0.65f;
				num3 *= 0.75f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (venom)
			{
				num2 *= 0.45f;
				num *= 0.75f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (midas)
			{
				num3 *= 0.3f;
				num *= 0.85f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (betsysCurse)
			{
				num *= 0.8f;
				num2 *= 0.6f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (oiled)
			{
				num *= 0.7f;
				num2 *= 0.7f;
				num3 *= 0.7f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (stinky)
			{
				num *= 0.7f;
				num3 *= 0.55f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (drippingSlime)
			{
				num *= 0.8f;
				num2 *= 0.8f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (drippingSparkleSlime)
			{
				num3 *= 0.85f;
				num2 *= 0.75f;
				npcColor = buffColor(npcColor, num, num2, num3, a);
			}
			if (ichor)
			{
				npcColor = new Color(255, 255, 0, 255);
			}
			if (CanApplyHunterPotionEffects() && lifeMax > 1)
			{
				byte b;
				byte b2;
				byte b3;
				if (friendly || catchItem > 0 || (damage == 0 && lifeMax == 5))
				{
					b = 50;
					b2 = byte.MaxValue;
					b3 = 50;
				}
				else
				{
					b = byte.MaxValue;
					b2 = 50;
					b3 = 50;
				}
				if (npcColor.R < b)
				{
					npcColor.R = b;
				}
				if (npcColor.G < b2)
				{
					npcColor.G = b2;
				}
				if (npcColor.B < b3)
				{
					npcColor.B = b3;
				}
			}
			return npcColor;
		}

