			AddBuff_RemoveOldMeleeBuffsOfMatchingType(type);
			AddBuff_ActuallyTryToAddTheBuff(type, num);
		}

		private bool AddBuff_ActuallyTryToAddTheBuff(int type, int time)
		{
			int num = -1;
