				{
					int num = 10;
					meleeCrit += num;
					rangedCrit += num;
					magicCrit += num;
					minionDamage += (float)num / 100f;
				}
				else if (buffType[j] == 2)
				{
					lifeRegen += 4;
				}
				else if (buffType[j] == 3)
				{
					moveSpeed += 0.25f;
				}
				else if (buffType[j] == 4)
				{
					gills = true;
				}
				else if (buffType[j] == 5)
				{
					statDefense += 8;
				}
