				}
			}
			if (num2 >= 1f)
			{
				torchLuck += 1f;
			}
			else if (num2 > 0f)
			{
				torchLuck += 0.5f;
			}
			if (num >= 2f)
			{
				torchLuck += -1.5f;
			}
			else if (num >= 1f)
			{
				torchLuck += -1f;
			}
			else if (num > 0f)
			{
				torchLuck += -0.5f;
			}
			dryCoralTorch = false;
			for (int i = 0; i < 24; i++)
			{
				nearbyTorch[i] = false;
			}
			if (torchLuck < 0f)
			{
				torchLuck = 0f;
			}
			if (torchGodCooldown <= 0 && !unlockedBiomeTorches && nearbyTorches > 100 && !happyFunTorchTime && (double)position.Y > Main.worldSurface * 16.0)
			{
				bool flag = false;
				for (int j = 0; j < inventory.Length; j++)
				{
					if (inventory[j].type == 5043)
					{
						flag = true;
						break;
					}
				}
				if (!flag)
				{
					happyFunTorchTime = true;
					numberOfTorchAttacksMade = 0;
				}
			}
			nearbyTorches = 0;
		}

