		private float CalculateCoinLuck()
		{
			if (coinLuck == 0f)
			{
				return 0f;
			}
			if (coinLuck > 249000f)
			{
				return 0.2f;
			}
			if (coinLuck > 24900f)
			{
				return 0.175f;
			}
			if (coinLuck > 24900f)
			{
				return 0.175f;
			}
			if (coinLuck > 2490f)
			{
				return 0.15f;
			}
			if (coinLuck > 249f)
			{
				return 0.125f;
			}
			if ((double)coinLuck > 24.9)
			{
				return 0.1f;
			}
			if ((double)coinLuck > 2.49)
			{
				return 0.075f;
			}
			if ((double)coinLuck > 0.249)
			{
				return 0.05f;
			}
			return 0.025f;
		}

