			}
			Point point = proj.Hitbox.ClosestPointInRect(compareSpot).ToTileCoordinates();
			if (!IsInTileInteractionRange(point.X, point.Y, TileReachCheckSettings.Simple))
			{
				return false;
			}
			return true;
		}

