		public void Spawn(PlayerSpawnContext context)
		{
			Main.LocalPlayer.creativeInterface = false;
			_funkytownAchievementCheckCooldown = 100;
			bool flag = false;
			if (context == PlayerSpawnContext.SpawningIntoWorld)
			{
