				NPC.ResetNetOffsets();
			}
			if (Main.netMode == 1 && whoAmI == Main.myPlayer)
			{
				NetMessage.SendData(12, -1, -1, null, Main.myPlayer, (int)(byte)context);
			}
			headPosition = Vector2.Zero;
			bodyPosition = Vector2.Zero;
			legPosition = Vector2.Zero;
