					{
						statLife = num;
					}
					breath = breathMax;
					if (spawnMax)
					{
						statLife = statLifeMax2;
						statMana = statManaMax2;
					}
				}
				immune = true;
				dead = false;
				immuneTime = 0;
			}
			active = true;
			Vector2 value = position;
