			{
				_ = SpawnX;
				_ = SpawnY;
				Spawn_SetPosition(SpawnX, SpawnY);
			}
			else
			{
				Spawn_SetPositionAtWorldSpawn();
			}
			wet = false;
			wetCount = 0;
			lavaWet = false;
			fallStart = (int)(position.Y / 16f);
			fallStart2 = fallStart;
			velocity.X = 0f;
			velocity.Y = 0f;
