		public float GetWeaponKnockback(Item sItem, float KnockBack)
		{
			if (sItem.summon)
			{
				KnockBack += minionKB;
			}
			if (sItem.melee && kbGlove)
			{
				KnockBack *= 2f;
			}
			if (kbBuff)
			{
				KnockBack *= 1.5f;
			}
			if (sItem.ranged && shroomiteStealth)
			{
				KnockBack *= 1f + (1f - stealth) * 0.5f;
			}
			if (sItem.ranged && setVortex)
			{
				KnockBack *= 1f + (1f - stealth) * 0.5f;
			}
			return KnockBack;
		}

