		public bool CheckMana(int amount, bool pay = false, bool blockQuickMana = false)
		{
			int num = (int)((float)amount * manaCost);
			if (statMana >= num)
			{
				if (pay)
				{
					statMana -= num;
				}
				return true;
			}
			if (manaFlower && !blockQuickMana)
			{
				QuickMana();
				if (statMana >= num)
				{
					if (pay)
					{
						statMana -= num;
					}
					return true;
				}
				return false;
			}
			return false;
		}

